import React, { useState, useRef, useEffect } from 'react';
import { Github, Globe, Play, X, ExternalLink, Volume2, VolumeX } from 'lucide-react';

const TrailerStyleProjectCard = ({ project }) => {
  // State management
  const [showModal, setShowModal] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(true); // Default to muted for autoplay
  const [videoError, setVideoError] = useState(false);

  // Refs
  const videoRef = useRef(null);
  const modalRef = useRef(null);
  const hoverTimerRef = useRef(null);

  // Check if project has video
  const hasVideo = project.demoVideo && project.demoVideo.length > 0;

  // Handle modal opening on hover or click
  const handleMouseEnter = () => {
    hoverTimerRef.current = setTimeout(() => {
      openModal();
    }, 500); // Delay to prevent accidental triggering
  };

  const handleMouseLeave = () => {
    if (hoverTimerRef.current) {
      clearTimeout(hoverTimerRef.current);
    }
  };

  const openModal = () => {
    setShowModal(true);
    document.body.style.overflow = 'hidden'; // Prevent scrolling when modal is open
  };

  const closeModal = () => {
    setShowModal(false);
    document.body.style.overflow = ''; // Re-enable scrolling

    // Stop video if playing
    if (videoRef.current) {
      videoRef.current.pause();
      videoRef.current.currentTime = 0;
      setIsPlaying(false);
    }
  };

  // Video playback functions
  const playVideo = () => {
    if (!videoRef.current) return;

    const playPromise = videoRef.current.play();

    if (playPromise !== undefined) {
      playPromise
        .then(() => {
          setIsPlaying(true);
          console.log("Video playing successfully");
        })
        .catch(error => {
          console.error("Error playing video:", error);

          // Try playing muted if unmuted playback fails
          if (!isMuted) {
            console.log("Trying muted playback");
            videoRef.current.muted = true;
            setIsMuted(true);

            videoRef.current.play()
              .then(() => setIsPlaying(true))
              .catch(e => {
                console.error("Failed even with muted playback:", e);
                setVideoError(true);
              });
          } else {
            setVideoError(true);
          }
        });
    }
  };

  const togglePlayPause = (e) => {
    e.stopPropagation();

    if (videoError) {
      setVideoError(false); // Reset error state for retry
    }

    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
        setIsPlaying(false);
      } else {
        playVideo();
      }
    }
  };

  const toggleMute = (e) => {
    e.stopPropagation();

    if (videoRef.current) {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  // Handle modal events
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showModal && modalRef.current && !modalRef.current.contains(e.target)) {
        closeModal();
      }
    };

    const handleEscKey = (e) => {
      if (showModal && e.key === 'Escape') {
        closeModal();
      }
    };

    if (showModal) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleEscKey);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscKey);
      if (hoverTimerRef.current) {
        clearTimeout(hoverTimerRef.current);
      }
    };
  }, [showModal]);

  // Autoplay video when modal opens
  useEffect(() => {
    if (showModal && hasVideo && videoRef.current) {
      setTimeout(() => {
        playVideo();
      }, 300); // Small delay to ensure modal is visible
    }
  }, [showModal]);

  return (
    <>
      {/* Project Card - Completely separate from modal */}
      <div 
        className="relative bg-white dark:bg-gray-700 rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-all hover:-translate-y-2 cursor-pointer"
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        onClick={openModal}
      >
        {/* Card Image Section */}
        <div className="h-48 relative overflow-hidden">
          <img 
            src={project.image} 
            alt={project.title}
            className="w-full h-full object-cover transition-all duration-500 hover:scale-110"
          />
          {/* Gradient overlay */}
          <div className="absolute inset-0 bg-gradient-to-t from-black to-transparent opacity-70"></div>

          {/* Title and tags */}
          <div className="absolute bottom-4 left-4 right-4 flex justify-between items-end">
            <div>
              <h3 className="text-xl font-bold text-white mb-1">{project.title}</h3>
              <span className="text-xs text-gray-300">{project.period}</span>
            </div>

            {/* Action buttons */}
            <div className="flex gap-2">
              {project.github && (
                <a 
                  href={project.github} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
                  title="GitHub Repository"
                  onClick={(e) => e.stopPropagation()}
                >
                  <Github size={18} className="text-white" />
                </a>
              )}
              {project.liveLink && (
                <a 
                  href={project.liveLink} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
                  title="Live Demo"
                  onClick={(e) => e.stopPropagation()}
                >
                  <Globe size={18} className="text-white" />
                </a>
              )}
            </div>
          </div>

          {/* Project tags */}
          <div className="absolute top-4 left-4 flex flex-wrap gap-2">
            {project.tags && project.tags.map((tag, i) => (
              <span key={i} className="px-3 py-1 text-xs font-bold bg-primary bg-opacity-90 text-white rounded-full shadow-md">
                {tag}
              </span>
            ))}
          </div>
        </div>

        {/* Card body with tech stack */}
        <div className="p-4">
          <div className="flex flex-wrap gap-2 mb-2">
            {project.tech.slice(0, 5).map((tech, i) => (
              <span key={i} className="px-2 py-1 text-xs bg-secondary dark:bg-tertiary text-primary dark:text-gray-800 rounded-full">
                {tech}
              </span>
            ))}
            {project.tech.length > 5 && (
              <span className="px-2 py-1 text-xs bg-gray-200 dark:bg-gray-600 text-gray-700 dark:text-gray-300 rounded-full">
                +{project.tech.length - 5} more
              </span>
            )}
          </div>

          {/* Brief description */}
          <p className="text-gray-700 dark:text-gray-300 text-sm line-clamp-2 mt-2">
            {project.description}
          </p>

          {/* Play indicator for video projects */}
          {hasVideo && (
            <div className="flex items-center justify-center mt-2">
              <div className="inline-flex items-center gap-2 text-primary dark:text-tertiary">
                <Play size={16} />
                <span className="text-sm">Watch demo</span>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Modal - completely separate from card */}
      {showModal && (
        <div className="fixed inset-0 z-50 overflow-auto bg-black/75 flex items-center justify-center p-4 animate-fadeIn">
          <div 
            ref={modalRef}
            className="bg-white dark:bg-gray-800 w-full max-w-6xl rounded-xl shadow-2xl overflow-hidden animate-fadeIn"
            style={{ animationDelay: '0.1s' }}
          >
            {/* Modal header */}
            <div className="bg-primary dark:bg-primary p-5 text-white relative">
              <h2 className="text-2xl md:text-3xl font-bold pr-8">{project.title}</h2>

              {/* Close button */}
              <button 
                className="absolute top-5 right-5 bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
                onClick={closeModal}
                aria-label="Close modal"
              >
                <X size={20} className="text-white" />
              </button>

              {/* Project period and tags */}
              <div className="flex flex-wrap items-center gap-2 mt-2">
                <span className="text-white/80 text-sm">{project.period}</span>
                <div className="h-4 w-px bg-white/30"></div>
                {project.tags && project.tags.map((tag, i) => (
                  <span key={i} className="px-3 py-1 text-xs font-medium bg-white/20 text-white rounded-full">
                    {tag}
                  </span>
                ))}
              </div>
            </div>

            {/* Modal content - responsive layout */}
            <div className="flex flex-col lg:flex-row">
              {/* Video/Image section */}
              <div className="lg:w-3/5 bg-black">
                {hasVideo ? (
                  <div className="relative">
                    {/* Video player */}
                    <video 
                      ref={videoRef}
                      className="w-full aspect-video object-contain"
                      src={project.demoVideo}
                      muted={isMuted}
                      loop={project.loop !== false}
                      playsInline
                      onEnded={() => !project.loop && setIsPlaying(false)}
                      onError={() => setVideoError(true)}
                    />

                    {/* Video controls */}
                    <div className="absolute inset-0 flex items-end p-4 bg-gradient-to-t from-black/70 to-transparent opacity-0 hover:opacity-100 transition-opacity duration-300">
                      <div className="flex justify-between w-full">
                        <button 
                          className="bg-white/20 p-3 rounded-full hover:bg-white/40 transition-all"
                          onClick={togglePlayPause}
                        >
                          {isPlaying ? (
                            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-white">
                              <rect x="6" y="4" width="4" height="16"></rect>
                              <rect x="14" y="4" width="4" height="16"></rect>
                            </svg>
                          ) : (
                            <Play size={24} className="text-white" />
                          )}
                        </button>

                        <button 
                          className="bg-white/20 p-3 rounded-full hover:bg-white/40 transition-all"
                          onClick={toggleMute}
                        >
                          {isMuted ? (
                            <VolumeX size={24} className="text-white" />
                          ) : (
                            <Volume2 size={24} className="text-white" />
                          )}
                        </button>
                      </div>
                    </div>

                    {/* Video error state */}
                    {videoError && (
                      <div className="absolute inset-0 flex items-center justify-center bg-black/70">
                        <div className="text-center p-6">
                          <div className="bg-primary/80 p-4 rounded-full inline-flex mb-4">
                            <Play size={32} className="text-white" />
                          </div>
                          <h3 className="text-white text-lg font-medium mb-3">Video playback error</h3>
                          <button 
                            className="px-4 py-2 bg-primary text-white rounded-lg"
                            onClick={togglePlayPause}
                          >
                            Retry
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="aspect-video">
                    <img 
                      src={project.image} 
                      alt={project.title}
                      className="w-full h-full object-cover"
                    />
                  </div>
                )}
              </div>

              {/* Project details section */}
              <div className="lg:w-2/5 p-6 overflow-y-auto max-h-[60vh] lg:max-h-[70vh]">
                <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">About the Project</h3>

                <p className="text-gray-700 dark:text-gray-300 mb-6">
                  {project.description}
                </p>

                <div className="mb-6">
                  <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-3">Technologies</h4>
                  <div className="flex flex-wrap gap-2">
                    {project.tech.map((tech, i) => (
                      <span key={i} className="px-3 py-1 text-sm bg-secondary dark:bg-tertiary text-primary dark:text-gray-800 rounded-lg">
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>

                <div className="flex flex-wrap gap-3">
                  {project.github && (
                    <a 
                      href={project.github} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2 bg-gray-100 dark:bg-gray-700 text-gray-800 dark:text-white rounded-lg hover:bg-gray-200 dark:hover:bg-gray-600 transition-all"
                    >
                      <Github size={20} />
                      <span>GitHub</span>
                    </a>
                  )}
                  {project.liveLink && (
                    <a 
                      href={project.liveLink} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2 bg-gray-100 dark:bg-gray-700 text-gray-800 dark:text-white rounded-lg hover:bg-gray-200 dark:hover:bg-gray-600 transition-all"
                    >
                      <Globe size={20} />
                      <span>Live Demo</span>
                    </a>
                  )}
                  {project.detailsLink && (
                    <a 
                      href={project.detailsLink}
                      className="flex items-center gap-2 px-4 py-2 bg-primary text-white rounded-lg hover:bg-opacity-90 transition-all"
                    >
                      <ExternalLink size={20} />
                      <span>View Details</span>
                    </a>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default TrailerStyleProjectCard;
