import React, { useState, useRef, useEffect } from 'react';
import { Github, Globe, Play, X, ExternalLink, Volume2, VolumeX } from 'lucide-react';

const TrailerStyleProjectCard = ({ project }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(true); // Default to muted for autoplay to work
  const [videoError, setVideoError] = useState(false);
  const videoRef = useRef(null);
  const modalRef = useRef(null);
  const timeoutRef = useRef(null);

  // Helper to determine if the project has a video
  const hasVideo = project.demoVideo && project.demoVideo.length > 0;

  // Handle video playback errors
  const handleVideoError = () => {
    console.error("Video playback error for:", project.title);
    setVideoError(true);
    setIsPlaying(false);
  };

  // Handle successful video loading
  const handleVideoLoaded = () => {
    console.log("Video loaded successfully for:", project.title);
    setVideoError(false);
  };

  useEffect(() => {
    // Add click outside handler to close modal
    const handleClickOutside = (e) => {
      if (isModalOpen && modalRef.current && !modalRef.current.contains(e.target)) {
        closeModal();
      }
    };

    // Add escape key handler
    const handleEscKey = (e) => {
      if (isModalOpen && e.key === 'Escape') {
        closeModal();
      }
    };

    if (isModalOpen) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleEscKey);
      document.body.style.overflow = 'hidden'; // Prevent scrolling when modal is open
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscKey);
      document.body.style.overflow = ''; // Re-enable scrolling
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [isModalOpen]);

  // Effect to autoplay video when modal opens
  useEffect(() => {
    if (isModalOpen && hasVideo && videoRef.current) {
      setTimeout(() => {
        playVideo();
      }, 300);
    }
  }, [isModalOpen]);

  // Handle card hover
  const handleCardHover = () => {
    // Use a timeout to prevent accidental hover triggering the modal
    timeoutRef.current = setTimeout(() => {
      openModal();
    }, 500);
  };

  const cancelHoverIntent = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    if (isPlaying && videoRef.current) {
      videoRef.current.pause();
      videoRef.current.currentTime = 0;
      setIsPlaying(false);
    }
  };

  // Helper function to play video with error handling
  const playVideo = () => {
    if (videoRef.current) {
      const playPromise = videoRef.current.play();

      if (playPromise !== undefined) {
        playPromise
          .then(() => {
            setIsPlaying(true);
            console.log("Video playing:", project.title);
          })
          .catch(error => {
            console.error("Error playing video:", error);
            // Most browsers require user interaction before playing videos with sound
            // Try again with muted option if it failed
            if (!isMuted) {
              console.log("Trying to play muted...");
              videoRef.current.muted = true;
              setIsMuted(true);
              videoRef.current.play()
                .then(() => {
                  setIsPlaying(true);
                  console.log("Video playing muted:", project.title);
                })
                .catch(e => {
                  console.error("Still cannot play video even when muted:", e);
                  setVideoError(true);
                });
            } else {
              setVideoError(true);
            }
          });
      }
    }
  };

  const togglePlayPause = (e) => {
    e.stopPropagation();
    if (videoError) {
      setVideoError(false); // Reset error state to try again
    }

    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
        setIsPlaying(false);
      } else {
        playVideo();
      }
    }
  };

  const toggleMute = (e) => {
    e.stopPropagation();
    if (videoRef.current) {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  return (
    <>
      {/* Regular project card - unchanged from your original design */}
      <div 
        className="bg-white dark:bg-gray-700 rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-all hover:-translate-y-2 animate-fadeIn relative group"
        onMouseEnter={handleCardHover}
        onMouseLeave={cancelHoverIntent}
        onClick={openModal}
      >
        <div className="h-48 relative overflow-hidden">
          <img 
            src={project.image} 
            alt={project.title}
            className="w-full h-full object-cover transition-all duration-500 hover:scale-110"
          />
          <div className="absolute inset-0 bg-gradient-to-t from-black to-transparent opacity-70"></div>
          <div className="absolute bottom-4 left-4 right-4 flex justify-between items-end">
            <div>
              <h3 className="text-xl font-bold text-white mb-1">{project.title}</h3>
              <span className="text-xs text-gray-300">{project.period}</span>
            </div>
            <div className="flex gap-2">
              {project.github && (
                <a 
                  href={project.github} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-white bg-opacity-20 p-2 rounded-full hover:bg-opacity-40 transition-all"
                  title="GitHub Repository"
                  onClick={(e) => e.stopPropagation()}
                >
                  <Github size={18} className="text-white" />
                </a>
              )}
              {project.liveLink && (
                <a 
                  href={project.liveLink} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-white bg-opacity-20 p-2 rounded-full hover:bg-opacity-40 transition-all"
                  title="Live Demo"
                  onClick={(e) => e.stopPropagation()}
                >
                  <Globe size={18} className="text-white" />
                </a>
              )}
            </div>
          </div>
          <div className="absolute top-4 left-4 flex flex-wrap gap-2">
            {project.tags && project.tags.map((tag, i) => (
              <span key={i} className="px-3 py-1 text-xs font-bold bg-primary bg-opacity-90 text-white rounded-full shadow-md">
                {tag}
              </span>
            ))}
          </div>
        </div>

        {/* Tech stack */}
        <div className="p-4">
          <div className="flex flex-wrap gap-2 mb-2">
            {project.tech.map((tech, i) => (
              <span key={i} className="px-2 py-1 text-xs bg-secondary dark:bg-tertiary text-primary dark:text-gray-800 rounded-full">
                {tech}
              </span>
            ))}
          </div>

          {/* Project description - visible on hover */}
          <div className="transition-all duration-300 overflow-hidden max-h-0 group-hover:max-h-40 opacity-0 group-hover:opacity-100">
            <p className="text-gray-700 dark:text-gray-300 text-sm">{project.description}</p>
          </div>

          {/* Hover instruction */}
          <div className="text-center text-sm text-gray-500 italic mt-2 group-hover:opacity-0 transition-opacity duration-300">
            Hover to see details
          </div>
        </div>

        {/* Trailer indicator for videos */}
        {hasVideo && (
          <div className="absolute top-[40%] left-1/2 transform -translate-x-1/2 -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
            <div className="bg-primary rounded-full p-4 shadow-lg">
              <Play size={32} className="text-white" fill="white" />
            </div>
          </div>
        )}
      </div>

      {/* Completely separate modal overlay */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center p-4 animate-fadeIn overflow-auto">
          <div 
            ref={modalRef}
            className="bg-white dark:bg-gray-800 rounded-xl overflow-hidden w-full max-w-5xl max-h-[90vh] shadow-2xl animate-fadeIn"
            style={{ animationDelay: '0.1s' }}
          >
            {/* Modal header with close button */}
            <div className="relative bg-primary p-6">
              <h2 className="text-2xl md:text-3xl font-bold text-white pr-8">{project.title}</h2>
              <button 
                className="absolute top-6 right-6 bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
                onClick={closeModal}
              >
                <X size={20} className="text-white" />
              </button>
              <div className="flex flex-wrap gap-2 mt-2">
                <span className="text-sm text-white/80">{project.period}</span>
                {project.tags && project.tags.map((tag, i) => (
                  <span key={i} className="px-3 py-1 text-xs font-medium bg-white/20 text-white rounded-full">
                    {tag}
                  </span>
                ))}
              </div>
            </div>

            {/* Modal body */}
            <div className="flex flex-col lg:flex-row">
              {/* Left side - video/image */}
              <div className="lg:w-7/12 bg-black">
                {hasVideo ? (
                  <div className="relative">
                    <video 
                      ref={videoRef}
                      className="w-full aspect-video object-contain"
                      src={project.demoVideo}
                      muted={isMuted}
                      loop={project.loop !== false}
                      playsInline
                      onEnded={() => !project.loop && setIsPlaying(false)}
                      onError={handleVideoError}
                      onLoadedData={handleVideoLoaded}
                    />

                    {/* Video controls */}
                    <div className="absolute bottom-4 left-4 right-4 flex justify-between opacity-0 hover:opacity-100 transition-opacity duration-300">
                      <button 
                        className="bg-black/50 hover:bg-black/70 p-3 rounded-full transition-all"
                        onClick={togglePlayPause}
                      >
                        {isPlaying ? (
                          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-white">
                            <rect x="6" y="4" width="4" height="16"></rect>
                            <rect x="14" y="4" width="4" height="16"></rect>
                          </svg>
                        ) : (
                          <Play size={24} className="text-white" />
                        )}
                      </button>

                      <button 
                        className="bg-black/50 hover:bg-black/70 p-3 rounded-full transition-all"
                        onClick={toggleMute}
                      >
                        {isMuted ? (
                          <VolumeX size={24} className="text-white" />
                        ) : (
                          <Volume2 size={24} className="text-white" />
                        )}
                      </button>
                    </div>

                    {/* Video error display */}
                    {videoError && (
                      <div className="absolute inset-0 bg-black/60 flex items-center justify-center">
                        <div className="text-center">
                          <div className="bg-primary/80 p-4 rounded-full mb-4 mx-auto inline-block">
                            <Play size={32} className="text-white" />
                          </div>
                          <p className="text-white text-lg font-medium">Video playback error</p>
                          <button 
                            className="mt-4 px-4 py-2 bg-primary text-white rounded-lg hover:bg-opacity-90"
                            onClick={togglePlayPause}
                          >
                            Retry
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                ) : (
                  <img 
                    src={project.image} 
                    alt={project.title}
                    className="w-full aspect-video object-cover"
                  />
                )}
              </div>

              {/* Right side - project details */}
              <div className="lg:w-5/12 p-6 overflow-y-auto">
                <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">About this project</h3>
                <p className="text-gray-700 dark:text-gray-300 mb-6">{project.description}</p>

                <div className="mb-6">
                  <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-3">Technologies</h4>
                  <div className="flex flex-wrap gap-2">
                    {project.tech.map((tech, i) => (
                      <span key={i} className="px-3 py-1 text-sm bg-secondary dark:bg-tertiary text-primary dark:text-gray-800 rounded-lg">
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>

                <div className="flex flex-wrap gap-3">
                  {project.github && (
                    <a 
                      href={project.github} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2 bg-gray-100 dark:bg-gray-700 text-gray-800 dark:text-white rounded-lg hover:bg-gray-200 dark:hover:bg-gray-600 transition-all"
                    >
                      <Github size={20} />
                      <span>GitHub</span>
                    </a>
                  )}
                  {project.liveLink && (
                    <a 
                      href={project.liveLink} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2 bg-gray-100 dark:bg-gray-700 text-gray-800 dark:text-white rounded-lg hover:bg-gray-200 dark:hover:bg-gray-600 transition-all"
                    >
                      <Globe size={20} />
                      <span>Live Demo</span>
                    </a>
                  )}
                  {project.detailsLink && (
                    <a 
                      href={project.detailsLink}
                      className="flex items-center gap-2 px-4 py-2 bg-primary text-white rounded-lg hover:bg-opacity-90 transition-all"
                    >
                      <ExternalLink size={20} />
                      <span>View Details</span>
                    </a>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default TrailerStyleProjectCard;
