import React from 'react';

const ExperienceSection = ({ experiences }) => (
  <section id="experience" className="py-32">
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      <h2 className="text-4xl font-bold mb-16 text-center">
        <span className="bg-gradient-to-r from-indigo-500 to-purple-500 bg-clip-text text-transparent">
          Experience
        </span>
      </h2>
      <div className="space-y-12">
        {experiences.map((exp, index) => (
          <div
            key={index}
            className="bg-gray-800/50 backdrop-blur-xl p-8 rounded-lg border border-gray-700/50 hover:border-indigo-500/50 transition-all duration-300"
          >
            <h3 className="text-2xl font-semibold text-white">{exp.company}</h3>
            <p className="text-indigo-400 mb-2">{exp.role}</p>
            <p className="text-gray-400 text-sm mb-4">{exp.period}</p>
            <ul className="space-y-2">
              {exp.achievements.map((achievement, i) => (
                <li key={i} className="text-gray-300 flex items-start">
                  <span className="text-indigo-500 mr-2">â€¢</span>
                  {achievement}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  </section>
);

export default ExperienceSection;
