import React, { useState, useRef, useEffect } from 'react';
import { Github, Globe, Play, X, ExternalLink, Volume2, VolumeX } from 'lucide-react';

const TrailerStyleProjectCard = ({ project }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(true); // Default to muted for autoplay to work
  const [videoError, setVideoError] = useState(false);
  const videoRef = useRef(null);
  const modalRef = useRef(null);
  const timeoutRef = useRef(null);

  // Helper to determine if the project has a video
  const hasVideo = project.demoVideo && project.demoVideo.length > 0;

  // Handle video playback errors
  const handleVideoError = () => {
    console.error("Video playback error for:", project.title);
    setVideoError(true);
    setIsPlaying(false);
  };

  // Handle successful video loading
  const handleVideoLoaded = () => {
    console.log("Video loaded successfully for:", project.title);
    setVideoError(false);
  };

  useEffect(() => {
    // Add click outside handler to close modal
    const handleClickOutside = (e) => {
      if (isModalOpen && modalRef.current && !modalRef.current.contains(e.target)) {
        closeModal();
      }
    };

    // Add escape key handler
    const handleEscKey = (e) => {
      if (isModalOpen && e.key === 'Escape') {
        closeModal();
      }
    };

    if (isModalOpen) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleEscKey);
      document.body.style.overflow = 'hidden'; // Prevent scrolling when modal is open
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscKey);
      document.body.style.overflow = ''; // Re-enable scrolling
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [isModalOpen]);

  // Effect to autoplay video when modal opens
  useEffect(() => {
    if (isModalOpen && hasVideo && videoRef.current) {
      setTimeout(() => {
        playVideo();
      }, 300);
    }
  }, [isModalOpen]);

  // Handle card hover
  const handleCardHover = () => {
    // Use a timeout to prevent accidental hover triggering the modal
    timeoutRef.current = setTimeout(() => {
      openModal();
    }, 500);
  };

  const cancelHoverIntent = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    if (isPlaying && videoRef.current) {
      videoRef.current.pause();
      videoRef.current.currentTime = 0;
      setIsPlaying(false);
    }
  };

  // Helper function to play video with error handling
  const playVideo = () => {
    if (videoRef.current) {
      const playPromise = videoRef.current.play();

      if (playPromise !== undefined) {
        playPromise
          .then(() => {
            setIsPlaying(true);
            console.log("Video playing:", project.title);
          })
          .catch(error => {
            console.error("Error playing video:", error);
            // Most browsers require user interaction before playing videos with sound
            // Try again with muted option if it failed
            if (!isMuted) {
              console.log("Trying to play muted...");
              videoRef.current.muted = true;
              setIsMuted(true);
              videoRef.current.play()
                .then(() => {
                  setIsPlaying(true);
                  console.log("Video playing muted:", project.title);
                })
                .catch(e => {
                  console.error("Still cannot play video even when muted:", e);
                  setVideoError(true);
                });
            } else {
              setVideoError(true);
            }
          });
      }
    }
  };

  const togglePlayPause = (e) => {
    e.stopPropagation();
    if (videoError) {
      setVideoError(false); // Reset error state to try again
    }

    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
        setIsPlaying(false);
      } else {
        playVideo();
      }
    }
  };

  const toggleMute = (e) => {
    e.stopPropagation();
    if (videoRef.current) {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  return (
    <>
      {/* Regular project card */}
      <div 
        className="bg-white dark:bg-gray-700 rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-all hover:-translate-y-2 cursor-pointer h-80"
        onMouseEnter={handleCardHover}
        onMouseLeave={cancelHoverIntent}
        onClick={openModal}
      >
        {/* Image thumbnail */}
        <div className="h-48 relative overflow-hidden">
          <img 
            src={project.image} 
            alt={project.title}
            className="w-full h-full object-cover transition-all duration-500 hover:scale-105"
          />
          <div className="absolute inset-0 bg-gradient-to-t from-black to-transparent opacity-70"></div>
          <div className="absolute bottom-4 left-4 right-4 flex justify-between items-end">
            <div>
              <h3 className="text-xl font-bold text-white mb-1">{project.title}</h3>
              <span className="text-xs text-gray-300">{project.period}</span>
            </div>
          </div>

          {/* Play indicator for videos */}
          {hasVideo && (
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="bg-primary bg-opacity-70 rounded-full p-3 transform transition-all duration-300 hover:scale-110">
                <Play size={24} className="text-white" />
              </div>
            </div>
          )}

          <div className="absolute top-4 left-4 flex flex-wrap gap-2">
            {project.tags && project.tags.map((tag, i) => (
              <span key={i} className="px-3 py-1 text-xs font-bold bg-primary bg-opacity-90 text-white rounded-full shadow-md">
                {tag}
              </span>
            ))}
          </div>
        </div>

        {/* Card footer with tech stack */}
        <div className="p-4">
          <div className="flex flex-wrap gap-2 mb-2">
            {project.tech.slice(0, 4).map((tech, i) => (
              <span key={i} className="px-2 py-1 text-xs bg-secondary dark:bg-tertiary text-primary dark:text-gray-800 rounded-full">
                {tech}
              </span>
            ))}
            {project.tech.length > 4 && (
              <span className="px-2 py-1 text-xs bg-gray-200 dark:bg-gray-600 text-gray-700 dark:text-gray-300 rounded-full">
                +{project.tech.length - 4} more
              </span>
            )}
          </div>

          <p className="text-gray-700 dark:text-gray-300 text-sm line-clamp-2">{project.description}</p>
        </div>
      </div>

      {/* Modal overlay */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center p-4 animate-fadeIn overflow-y-auto">
          {/* Modal container */}
          <div 
            ref={modalRef}
            className="bg-white dark:bg-gray-800 rounded-xl overflow-hidden w-full max-w-6xl max-h-[90vh] shadow-2xl animate-fadeIn"
            style={{ animationDelay: '0.1s' }}
          >
            {/* Modal header */}
            <div className="relative bg-primary dark:bg-primary p-6">
              <button 
                className="absolute top-6 right-6 bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
                onClick={closeModal}
              >
                <X size={20} className="text-white" />
              </button>
              <h2 className="text-2xl md:text-3xl font-bold text-white">{project.title}</h2>
              <div className="flex flex-wrap items-center gap-2 mt-2">
                <span className="text-sm text-white/80">{project.period}</span>
                <div className="h-4 w-px bg-white/30"></div>
                <div className="flex flex-wrap gap-2">
                  {project.tags && project.tags.map((tag, i) => (
                    <span key={i} className="px-3 py-1 text-xs font-medium bg-white/20 text-white rounded-full">
                      {tag}
                    </span>
                  ))}
                </div>
              </div>
            </div>

            {/* Modal body */}
            <div className="flex flex-col md:flex-row">
              {/* Video/media section */}
              <div className="md:w-3/5 bg-black">
                {hasVideo && !videoError ? (
                  <div className="relative">
                    <video 
                      ref={videoRef}
                      className="w-full aspect-video object-contain"
                      src={project.demoVideo}
                      muted={isMuted}
                      loop={project.loop !== false}
                      playsInline
                      onEnded={() => !project.loop && setIsPlaying(false)}
                      onError={handleVideoError}
                      onLoadedData={handleVideoLoaded}
                    />

                    {/* Video controls overlay */}
                    <div className="absolute inset-0 flex flex-col justify-between p-4 bg-gradient-to-t from-black/60 to-transparent opacity-0 hover:opacity-100 transition-opacity duration-300">
                      <div></div> {/* Spacer */}
                      <div className="flex items-center justify-between">
                        <button 
                          className="bg-white/20 p-3 rounded-full hover:bg-white/40 transition-all"
                          onClick={togglePlayPause}
                        >
                          {isPlaying ? (
                            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-white">
                              <rect x="6" y="4" width="4" height="16"></rect>
                              <rect x="14" y="4" width="4" height="16"></rect>
                            </svg>
                          ) : (
                            <Play size={24} className="text-white" />
                          )}
                        </button>
                        <button 
                          className="bg-white/20 p-3 rounded-full hover:bg-white/40 transition-all"
                          onClick={toggleMute}
                        >
                          {isMuted ? (
                            <VolumeX size={24} className="text-white" />
                          ) : (
                            <Volume2 size={24} className="text-white" />
                          )}
                        </button>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="relative">
                    <img 
                      src={project.image} 
                      alt={project.title}
                      className="w-full aspect-video object-cover"
                    />

                    {/* Play button overlay for video error state */}
                    {hasVideo && videoError && (
                      <div 
                        className="absolute inset-0 bg-black/50 flex items-center justify-center cursor-pointer"
                        onClick={togglePlayPause}
                      >
                        <div className="flex flex-col items-center">
                          <Play size={48} className="text-white opacity-90 hover:opacity-100" />
                          <span className="text-sm text-white mt-2">Retry Video</span>
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </div>

              {/* Project details section */}
              <div className="md:w-2/5 p-6 md:max-h-[60vh] overflow-y-auto">
                <div className="mb-6">
                  <h3 className="text-lg font-bold text-gray-900 dark:text-white mb-3">Description</h3>
                  <p className="text-gray-700 dark:text-gray-300">{project.description}</p>
                </div>

                <div className="mb-6">
                  <h3 className="text-lg font-bold text-gray-900 dark:text-white mb-3">Technologies</h3>
                  <div className="flex flex-wrap gap-2">
                    {project.tech.map((tech, i) => (
                      <span key={i} className="px-3 py-1 text-sm bg-secondary dark:bg-tertiary text-primary dark:text-gray-800 rounded-lg">
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>

                <div className="flex flex-wrap gap-3">
                  {project.github && (
                    <a 
                      href={project.github} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="inline-flex items-center gap-2 px-4 py-2 bg-gray-100 dark:bg-gray-700 rounded-lg text-gray-800 dark:text-white hover:bg-gray-200 dark:hover:bg-gray-600 transition-all"
                      onClick={(e) => e.stopPropagation()}
                    >
                      <Github size={20} />
                      <span>GitHub</span>
                    </a>
                  )}
                  {project.liveLink && (
                    <a 
                      href={project.liveLink} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="inline-flex items-center gap-2 px-4 py-2 bg-gray-100 dark:bg-gray-700 rounded-lg text-gray-800 dark:text-white hover:bg-gray-200 dark:hover:bg-gray-600 transition-all"
                      onClick={(e) => e.stopPropagation()}
                    >
                      <Globe size={20} />
                      <span>Live Demo</span>
                    </a>
                  )}
                  {project.detailsLink && (
                    <a 
                      href={project.detailsLink}
                      className="inline-flex items-center gap-2 px-4 py-2 bg-primary dark:bg-primary rounded-lg text-white hover:bg-opacity-90 transition-all"
                      onClick={(e) => e.stopPropagation()}
                    >
                      <ExternalLink size={20} />
                      <span>View Details</span>
                    </a>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default TrailerStyleProjectCard;
