import React, { useState, useRef, useEffect } from 'react';
import { Github, Globe, Play, X, ExternalLink, Volume2, VolumeX, Maximize, Minimize } from 'lucide-react';

const TrailerStyleProjectCard = ({ project }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(project.muted !== false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const videoRef = useRef(null);
  const cardRef = useRef(null);

  // Helper to determine if the project has a video
  const hasVideo = project.demoVideo && project.demoVideo.length > 0;

  useEffect(() => {
    // Handle escape key to exit fullscreen
    const handleKeyDown = (e) => {
      if (e.key === 'Escape' && isFullscreen) {
        setIsFullscreen(false);
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isFullscreen]);

  useEffect(() => {
    // Handle document click to exit fullscreen
    const handleDocumentClick = (e) => {
      if (isFullscreen && cardRef.current && !cardRef.current.contains(e.target)) {
        setIsFullscreen(false);
      }
    };

    if (isFullscreen) {
      document.addEventListener('click', handleDocumentClick);
    }

    return () => document.removeEventListener('click', handleDocumentClick);
  }, [isFullscreen]);

  const handleMouseEnter = () => {
    if (!isFullscreen) {
      setIsHovered(true);
      // If there's a video and autoplay is enabled, start playing after a small delay
      if (hasVideo && project.autoplay) {
        setTimeout(() => {
          if (videoRef.current) {
            videoRef.current.play().catch(err => console.log('Autoplay prevented:', err));
            setIsPlaying(true);
          }
        }, 700);
      }
    }
  };

  const handleMouseLeave = () => {
    if (!isFullscreen) {
      setIsHovered(false);
      // If a video is playing, pause it
      if (isPlaying && videoRef.current && !isFullscreen) {
        videoRef.current.pause();
        videoRef.current.currentTime = 0;
        setIsPlaying(false);
      }
    }
  };

  const togglePlayPause = (e) => {
    e.stopPropagation();
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play().catch(err => console.log('Play prevented:', err));
      }
      setIsPlaying(!isPlaying);
    }
  };

  const toggleMute = (e) => {
    e.stopPropagation();
    if (videoRef.current) {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  const toggleFullscreen = (e) => {
    e.stopPropagation();
    setIsFullscreen(!isFullscreen);

    // If entering fullscreen, ensure video is playing
    if (!isFullscreen && hasVideo && !isPlaying && videoRef.current) {
      videoRef.current.play().catch(err => console.log('Play prevented:', err));
      setIsPlaying(true);
    }
  };

  // Determine card style based on fullscreen state
  const cardStyle = isFullscreen
    ? "fixed inset-0 z-50 bg-black flex items-center justify-center p-8"
    : "relative rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-all duration-500 h-80 group";

  return (
    <div 
      ref={cardRef}
      className={cardStyle}
      onMouseEnter={!isFullscreen ? handleMouseEnter : undefined}
      onMouseLeave={!isFullscreen ? handleMouseLeave : undefined}
    >
      {/* Project image as background (always visible unless fullscreen) */}
      {!isFullscreen && (
        <div className="absolute inset-0 bg-gray-900">
          <img 
            src={project.image || "/api/placeholder/600/400"} 
            alt={project.title}
            className={`w-full h-full object-cover transition-all duration-700 ${isHovered ? 'scale-105 opacity-50' : 'scale-100'}`}
          />
        </div>
      )}

      {/* Gradient overlay for better text visibility (unless fullscreen) */}
      {!isFullscreen && (
        <div className="absolute inset-0 bg-gradient-to-t from-black via-black/60 to-transparent opacity-70"></div>
      )}

      {/* Project metadata (always visible with animation, unless fullscreen) */}
      {!isFullscreen && (
        <div className="absolute bottom-0 left-0 right-0 p-4 z-10 transform transition-transform duration-300">
          <div className="flex justify-between items-end">
            <div className={`transition-all duration-500 ${isHovered ? 'translate-y-0 opacity-100' : 'translate-y-4 opacity-90'}`}>
              <h3 className="text-xl font-bold text-white mb-1">{project.title}</h3>
              <div className="flex items-center space-x-2">
                <span className="text-xs text-gray-300">{project.period}</span>
                {project.tags && project.tags.map((tag, i) => (
                  <span key={i} className="px-2 py-0.5 text-xs font-medium bg-primary text-white rounded-full">
                    {tag}
                  </span>
                ))}
              </div>
            </div>

            {/* Action buttons */}
            <div className={`flex gap-2 transition-all duration-500 ${isHovered ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'}`}>
              {project.github && (
                <a 
                  href={project.github} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
                  title="GitHub Repository"
                >
                  <Github size={18} className="text-white" />
                </a>
              )}
              {project.liveLink && (
                <a 
                  href={project.liveLink} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
                  title="Live Demo"
                >
                  <Globe size={18} className="text-white" />
                </a>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Tech stack pills (visible unless fullscreen) */}
      {!isFullscreen && (
        <div className="absolute top-4 left-4 right-4 flex flex-wrap gap-2 z-10">
          {project.tech && project.tech.map((tech, i) => (
            <span 
              key={i} 
              className={`px-2 py-1 text-xs bg-tertiary/80 text-white rounded-full transition-all duration-300 ${isHovered ? 'opacity-100' : 'opacity-70'}`}
            >
              {tech}
            </span>
          ))}
        </div>
      )}

      {/* Expanded content on hover or fullscreen */}
      <div 
        className={`
          ${isFullscreen 
            ? 'absolute inset-0 bg-black flex flex-col items-center justify-center p-8' 
            : `absolute inset-0 bg-gradient-to-t from-black to-black/80 flex flex-col justify-center p-6 transition-all duration-500 ease-in-out z-20 overflow-hidden ${
                isHovered ? 'opacity-100' : 'opacity-0 pointer-events-none'
              }`
          }
        `}
      >
        {/* Close button - always visible in fullscreen */}
        {(isHovered || isFullscreen) && (
          <button 
            className={`${isFullscreen ? 'absolute top-8 right-8' : 'absolute top-4 right-4'} bg-white/20 p-1 rounded-full hover:bg-white/40 transition-all z-30`}
            onClick={(e) => {
              e.stopPropagation();
              if (isFullscreen) {
                setIsFullscreen(false);
              } else {
                handleMouseLeave();
              }
            }}
          >
            <X size={isFullscreen ? 24 : 16} className="text-white" />
          </button>
        )}

        {/* Project title - only visible in fullscreen */}
        {isFullscreen && (
          <div className="absolute top-8 left-8 z-30">
            <h2 className="text-3xl font-bold text-white">{project.title}</h2>
            <div className="flex items-center space-x-2 mt-2">
              <span className="text-sm text-gray-300">{project.period}</span>
              {project.tags && project.tags.map((tag, i) => (
                <span key={i} className="px-3 py-1 text-xs font-medium bg-primary text-white rounded-full">
                  {tag}
                </span>
              ))}
            </div>
          </div>
        )}

        {/* Project description - different styles for fullscreen vs hover */}
        {!isFullscreen ? (
          <div className="mb-4 max-h-40 overflow-y-auto">
            <p className="text-white/90 text-sm leading-relaxed">{project.description}</p>
          </div>
        ) : (
          <div className="absolute bottom-8 left-8 right-8 max-w-lg">
            <p className="text-white/90 text-sm leading-relaxed">{project.description}</p>
            <div className="flex gap-4 mt-4">
              {project.github && (
                <a 
                  href={project.github} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="flex items-center gap-2 text-white/90 hover:text-white"
                >
                  <Github size={18} />
                  <span>GitHub</span>
                </a>
              )}
              {project.liveLink && (
                <a 
                  href={project.liveLink} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="flex items-center gap-2 text-white/90 hover:text-white"
                >
                  <Globe size={18} />
                  <span>Live Demo</span>
                </a>
              )}
              {project.detailsLink && (
                <a 
                  href={project.detailsLink}
                  className="flex items-center gap-2 text-white/90 hover:text-white"
                >
                  <ExternalLink size={18} />
                  <span>Details</span>
                </a>
              )}
            </div>
          </div>
        )}

        {/* Video player */}
        {hasVideo && (
          <div className={`relative ${isFullscreen ? 'w-full max-w-4xl mx-auto' : 'rounded-lg overflow-hidden mt-auto mb-16'}`}>
            {isPlaying || isFullscreen ? (
              <div className="relative">
                <video 
                  ref={videoRef}
                  className={`${isFullscreen ? 'w-full object-contain max-h-[70vh]' : 'w-full h-32 object-cover'} rounded-lg`}
                  src={project.demoVideo}
                  muted={isMuted}
                  loop={project.loop !== false}
                  controls={isFullscreen}
                  autoPlay={isFullscreen}
                  onEnded={() => !project.loop && setIsPlaying(false)}
                />

                {/* Video controls for hover state */}
                {!isFullscreen && (
                  <div className="absolute bottom-2 right-2 flex gap-2">
                    <button 
                      className="bg-black/60 p-1 rounded-full hover:bg-black/80 transition-all"
                      onClick={toggleMute}
                    >
                      {isMuted ? (
                        <VolumeX size={16} className="text-white" />
                      ) : (
                        <Volume2 size={16} className="text-white" />
                      )}
                    </button>
                    <button 
                      className="bg-black/60 p-1 rounded-full hover:bg-black/80 transition-all"
                      onClick={toggleFullscreen}
                    >
                      <Maximize size={16} className="text-white" />
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div 
                className="bg-black/50 h-24 flex items-center justify-center cursor-pointer rounded-lg"
                onClick={togglePlayPause}
              >
                <div className="flex flex-col items-center">
                  <Play size={36} className="text-white opacity-90 hover:opacity-100" />
                  <span className="text-xs text-white mt-2">Play Demo</span>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Fullscreen controls - only in fullscreen mode */}
        {isFullscreen && hasVideo && (
          <div className="absolute bottom-8 right-8 flex gap-4">
            <button 
              className="bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
              onClick={toggleMute}
            >
              {isMuted ? (
                <VolumeX size={20} className="text-white" />
              ) : (
                <Volume2 size={20} className="text-white" />
              )}
            </button>
            <button 
              className="bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
              onClick={toggleFullscreen}
            >
              <Minimize size={20} className="text-white" />
            </button>
          </div>
        )}

        {/* View details button - only visible in hover mode */}
        {!isFullscreen && project.detailsLink && (
          <a 
            href={project.detailsLink}
            className="flex items-center justify-center gap-2 mt-4 px-4 py-2 bg-primary text-white rounded-lg hover:bg-opacity-90 transition-all"
          >
            <span>View Details</span>
            <ExternalLink size={16} />
          </a>
        )}
      </div>

      {/* Fullscreen toggle button - always visible */}
      {hasVideo && !isFullscreen && isHovered && (
        <button 
          className="absolute top-12 right-4 bg-white/20 p-1.5 rounded-full hover:bg-white/40 transition-all z-30"
          onClick={toggleFullscreen}
          title="Fullscreen"
        >
          <Maximize size={16} className="text-white" />
        </button>
      )}
    </div>
  );
};

export default TrailerStyleProjectCard;
