import React, { useState, useRef, useEffect } from 'react';
import { Github, Globe, Play, X, ExternalLink, Volume2, VolumeX } from 'lucide-react';

const TrailerStyleProjectCard = ({ project }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(true); // Default to muted for autoplay to work
  const [videoError, setVideoError] = useState(false);
  const videoRef = useRef(null);
  const cardRef = useRef(null);
  const timeoutRef = useRef(null);

  // Helper to determine if the project has a video
  const hasVideo = project.demoVideo && project.demoVideo.length > 0;

  // Handle video playback errors
  const handleVideoError = () => {
    console.error("Video playback error for:", project.title);
    setVideoError(true);
    setIsPlaying(false);
  };

  // Handle successful video loading
  const handleVideoLoaded = () => {
    console.log("Video loaded successfully for:", project.title);
    setVideoError(false);
  };

  useEffect(() => {
    // Add click outside handler to close expanded card
    const handleClickOutside = (e) => {
      if (isExpanded && cardRef.current && !cardRef.current.contains(e.target)) {
        closeCard();
      }
    };

    if (isExpanded) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [isExpanded]);

  // Auto expand and play on hover
  const handleMouseEnter = () => {
    // Use a timeout to prevent accidental hover triggering the modal
    timeoutRef.current = setTimeout(() => {
      setIsExpanded(true);
      if (hasVideo) {
        setTimeout(() => {
          playVideo();
        }, 300); // Short delay to let modal animation finish
      }
    }, 500); // Delay to prevent accidental hover
  };

  const handleMouseLeave = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  };

  const closeCard = () => {
    setIsExpanded(false);
    if (isPlaying && videoRef.current) {
      videoRef.current.pause();
      videoRef.current.currentTime = 0;
      setIsPlaying(false);
    }
  };

  // Helper function to play video with error handling
  const playVideo = () => {
    if (videoRef.current) {
      const playPromise = videoRef.current.play();

      if (playPromise !== undefined) {
        playPromise
          .then(() => {
            setIsPlaying(true);
            console.log("Video playing:", project.title);
          })
          .catch(error => {
            console.error("Error playing video:", error);
            // Most browsers require user interaction before playing videos with sound
            // Try again with muted option if it failed
            if (!isMuted) {
              console.log("Trying to play muted...");
              videoRef.current.muted = true;
              setIsMuted(true);
              videoRef.current.play()
                .then(() => {
                  setIsPlaying(true);
                  console.log("Video playing muted:", project.title);
                })
                .catch(e => {
                  console.error("Still cannot play video even when muted:", e);
                  setVideoError(true);
                });
            } else {
              setVideoError(true);
            }
          });
      }
    }
  };

  const togglePlayPause = (e) => {
    e.stopPropagation();
    if (videoError) {
      setVideoError(false); // Reset error state to try again
    }

    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
        setIsPlaying(false);
      } else {
        playVideo();
      }
    }
  };

  const toggleMute = (e) => {
    e.stopPropagation();
    if (videoRef.current) {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  return (
    <>
      {/* Regular card (always visible) */}
      <div 
        ref={cardRef}
        className="relative rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-all duration-500 h-80 group cursor-pointer"
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        onClick={() => setIsExpanded(true)}
      >
        {/* Project image as background */}
        <div className="absolute inset-0 bg-gray-900">
          <img 
            src={project.image || "/api/placeholder/600/400"} 
            alt={project.title}
            className="w-full h-full object-cover transition-all duration-700"
          />
        </div>

        {/* Gradient overlay for better text visibility */}
        <div className="absolute inset-0 bg-gradient-to-t from-black via-black/60 to-transparent opacity-70"></div>

        {/* Project metadata */}
        <div className="absolute bottom-0 left-0 right-0 p-4 z-10">
          <div className="flex justify-between items-end">
            <div>
              <h3 className="text-xl font-bold text-white mb-1">{project.title}</h3>
              <div className="flex items-center space-x-2">
                <span className="text-xs text-gray-300">{project.period}</span>
                {project.tags && project.tags.map((tag, i) => (
                  <span key={i} className="px-2 py-0.5 text-xs font-medium bg-primary text-white rounded-full">
                    {tag}
                  </span>
                ))}
              </div>
            </div>

            {/* Action buttons */}
            <div className="flex gap-2">
              {project.github && (
                <a 
                  href={project.github} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
                  title="GitHub Repository"
                  onClick={(e) => e.stopPropagation()}
                >
                  <Github size={18} className="text-white" />
                </a>
              )}
              {project.liveLink && (
                <a 
                  href={project.liveLink} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all"
                  title="Live Demo"
                  onClick={(e) => e.stopPropagation()}
                >
                  <Globe size={18} className="text-white" />
                </a>
              )}
            </div>
          </div>
        </div>

        {/* Tech stack pills */}
        <div className="absolute top-4 left-4 right-4 flex flex-wrap gap-2 z-10">
          {project.tech && project.tech.map((tech, i) => (
            <span 
              key={i} 
              className="px-2 py-1 text-xs bg-tertiary/80 text-white rounded-full"
            >
              {tech}
            </span>
          ))}
        </div>

        {/* Play indicator overlay when has video */}
        {hasVideo && (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="bg-primary/60 rounded-full p-4 transform transition-all duration-300 group-hover:scale-110">
              <Play size={30} className="text-white" />
            </div>
          </div>
        )}
      </div>

      {/* Expanded modal overlay */}
      {isExpanded && (
        <div className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center p-4 md:p-8 animate-fadeIn">
          <div 
            className="bg-gray-900 rounded-xl overflow-hidden max-w-5xl w-full max-h-[90vh] flex flex-col shadow-2xl animate-fadeIn relative"
            style={{ animationDelay: '0.1s' }}
          >
            {/* Close button */}
            <button 
              className="absolute top-4 right-4 bg-white/20 p-2 rounded-full hover:bg-white/40 transition-all z-30"
              onClick={closeCard}
            >
              <X size={20} className="text-white" />
            </button>

            {/* Two-column layout for larger screens */}
            <div className="flex flex-col md:flex-row h-full">
              {/* Video/Image section */}
              <div className="md:w-3/5 relative bg-black">
                {hasVideo && !videoError ? (
                  <div className="relative aspect-video">
                    <video 
                      ref={videoRef}
                      className="w-full h-full object-contain"
                      src={project.demoVideo}
                      muted={isMuted}
                      loop={project.loop !== false}
                      playsInline
                      autoPlay
                      controls={false}
                      onEnded={() => !project.loop && setIsPlaying(false)}
                      onError={handleVideoError}
                      onLoadedData={handleVideoLoaded}
                    />

                    {/* Video controls */}
                    <div className="absolute bottom-4 right-4 flex gap-2">
                      <button 
                        className="bg-black/60 p-2 rounded-full hover:bg-black/80 transition-all"
                        onClick={togglePlayPause}
                      >
                        {isPlaying ? (
                          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-white">
                            <rect x="6" y="4" width="4" height="16"></rect>
                            <rect x="14" y="4" width="4" height="16"></rect>
                          </svg>
                        ) : (
                          <Play size={24} className="text-white" />
                        )}
                      </button>
                      <button 
                        className="bg-black/60 p-2 rounded-full hover:bg-black/80 transition-all"
                        onClick={toggleMute}
                      >
                        {isMuted ? (
                          <VolumeX size={24} className="text-white" />
                        ) : (
                          <Volume2 size={24} className="text-white" />
                        )}
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="aspect-video relative overflow-hidden">
                    <img 
                      src={project.image || "/api/placeholder/800/600"} 
                      alt={project.title}
                      className="w-full h-full object-cover"
                    />

                    {/* Play button overlay for video error state */}
                    {hasVideo && videoError && (
                      <div 
                        className="absolute inset-0 bg-black/50 flex items-center justify-center cursor-pointer"
                        onClick={togglePlayPause}
                      >
                        <div className="flex flex-col items-center">
                          <Play size={48} className="text-white opacity-90 hover:opacity-100" />
                          <span className="text-sm text-white mt-2">Retry Video</span>
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </div>

              {/* Project details section */}
              <div className="md:w-2/5 p-6 overflow-y-auto">
                <h2 className="text-2xl font-bold text-white mb-2">{project.title}</h2>
                <div className="flex flex-wrap gap-2 mb-4">
                  <span className="text-sm text-gray-300">{project.period}</span>
                  {project.tags && project.tags.map((tag, i) => (
                    <span key={i} className="px-3 py-1 text-xs font-medium bg-primary text-white rounded-full">
                      {tag}
                    </span>
                  ))}
                </div>

                {/* Technology stack */}
                <div className="mb-4">
                  <h3 className="text-lg font-semibold text-white mb-2">Technologies</h3>
                  <div className="flex flex-wrap gap-2">
                    {project.tech && project.tech.map((tech, i) => (
                      <span key={i} className="px-2 py-1 text-xs bg-tertiary/30 text-white rounded">
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>

                {/* Project description */}
                <div className="mb-6">
                  <h3 className="text-lg font-semibold text-white mb-2">Description</h3>
                  <p className="text-gray-300 text-sm leading-relaxed">{project.description}</p>
                </div>

                {/* Action links */}
                <div className="flex flex-wrap gap-4">
                  {project.github && (
                    <a 
                      href={project.github} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2 bg-white/10 rounded-lg text-white hover:bg-white/20 transition-all"
                    >
                      <Github size={18} />
                      <span>GitHub</span>
                    </a>
                  )}
                  {project.liveLink && (
                    <a 
                      href={project.liveLink} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2 bg-white/10 rounded-lg text-white hover:bg-white/20 transition-all"
                    >
                      <Globe size={18} />
                      <span>Live Demo</span>
                    </a>
                  )}
                  {project.detailsLink && (
                    <a 
                      href={project.detailsLink}
                      className="flex items-center gap-2 px-4 py-2 bg-primary rounded-lg text-white hover:bg-opacity-90 transition-all"
                    >
                      <ExternalLink size={18} />
                      <span>View Details</span>
                    </a>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default TrailerStyleProjectCard;
