import React, { useState } from 'react';
import { Calendar, Clock, ChevronRight, Search } from 'lucide-react';

const BlogSection = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [activeCategory, setActiveCategory] = useState('All');

  // Sample blog posts data
  const blogPosts = [
    {
      id: 1,
      title: "Building Scalable Microservices with Go",
      excerpt: "Learn how to design and implement robust microservices architecture using Go, Docker, and Kubernetes.",
      date: "2024-03-15",
      readTime: "8 min",
      category: "Backend",
      image: "/api/placeholder/800/600",
      tags: ["Go", "Microservices", "Docker"]
    },
    {
      id: 2,
      title: "React Performance Optimization Techniques",
      excerpt: "Discover advanced techniques to improve your React application's performance and user experience.",
      date: "2024-03-01",
      readTime: "6 min",
      category: "Frontend",
      image: "/api/placeholder/800/600",
      tags: ["React", "Performance", "JavaScript"]
    },
    {
      id: 3,
      title: "Machine Learning in Production",
      excerpt: "Best practices for deploying and maintaining machine learning models in production environments.",
      date: "2024-02-15",
      readTime: "10 min",
      category: "ML/AI",
      image: "/api/placeholder/800/600",
      tags: ["Python", "ML", "DevOps"]
    }
  ];

  const categories = ['All', 'Frontend', 'Backend', 'ML/AI', 'DevOps'];

  // Filter posts based on search query and active category
  const filteredPosts = blogPosts.filter(post => {
    const matchesSearch = post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         post.excerpt.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         post.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesCategory = activeCategory === 'All' || post.category === activeCategory;
    return matchesSearch && matchesCategory;
  });

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <section id="blog" className="py-32 bg-gray-900/50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <h2 className="text-4xl font-bold mb-16 text-center">
          <span className="bg-gradient-to-r from-indigo-500 to-purple-500 bg-clip-text text-transparent">
            Blog
          </span>
        </h2>

        {/* Search and Filter */}
        <div className="mb-12 space-y-6">
          {/* Search Bar */}
          <div className="relative max-w-xl mx-auto">
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Search articles..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-12 pr-4 py-3 bg-gray-800/50 backdrop-blur-xl rounded-lg border border-gray-700/50 focus:border-indigo-500/50 focus:outline-none focus:ring-1 focus:ring-indigo-500/50 text-white placeholder-gray-400"
            />
          </div>

          {/* Categories */}
          <div className="flex flex-wrap justify-center gap-4">
            {categories.map((category) => (
              <button
                key={category}
                onClick={() => setActiveCategory(category)}
                className={`px-4 py-2 rounded-full text-sm transition-all duration-300 ${
                  activeCategory === category
                    ? 'bg-indigo-500 text-white'
                    : 'bg-gray-800/50 text-gray-300 hover:bg-indigo-500/10 hover:text-indigo-400'
                }`}
              >
                {category}
              </button>
            ))}
          </div>
        </div>

        {/* Blog Posts Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredPosts.map((post) => (
            <article
              key={post.id}
              className="group bg-gray-800/50 backdrop-blur-xl rounded-lg border border-gray-700/50 hover:border-indigo-500/50 transition-all duration-300 overflow-hidden"
            >
              {/* Post Image */}
              <div className="relative h-48 overflow-hidden">
                <img
                  src={post.image}
                  alt={post.title}
                  className="w-full h-full object-cover transform group-hover:scale-105 transition-transform duration-300"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-gray-900 via-gray-900/40 to-transparent opacity-60 group-hover:opacity-40 transition-opacity duration-300" />
              </div>

              {/* Post Content */}
              <div className="p-6">
                {/* Tags */}
                <div className="flex flex-wrap gap-2 mb-4">
                  {post.tags.map((tag, index) => (
                    <span
                      key={index}
                      className="px-2 py-1 text-xs bg-indigo-500/10 text-indigo-400 rounded-full border border-indigo-500/20"
                    >
                      {tag}
                    </span>
                  ))}
                </div>

                {/* Title */}
                <h3 className="text-xl font-semibold text-white mb-3 group-hover:text-indigo-400 transition-colors">
                  {post.title}
                </h3>

                {/* Excerpt */}
                <p className="text-gray-300 text-sm mb-4">
                  {post.excerpt}
                </p>

                {/* Metadata */}
                <div className="flex items-center justify-between text-sm text-gray-400">
                  <div className="flex items-center space-x-4">
                    <span className="flex items-center">
                      <Calendar size={16} className="mr-1" />
                      {formatDate(post.date)}
                    </span>
                    <span className="flex items-center">
                      <Clock size={16} className="mr-1" />
                      {post.readTime}
                    </span>
                  </div>
                  <button className="flex items-center text-indigo-400 hover:text-indigo-300 transition-colors">
                    Read More
                    <ChevronRight size={16} className="ml-1" />
                  </button>
                </div>
              </div>
            </article>
          ))}
        </div>
      </div>
    </section>
  );
};

export default BlogSection;
