import React, { useState, useEffect } from 'react';
import { Github, Linkedin, Mail, ExternalLink, Menu, X, ArrowRight, Star, Code, Layout, Database, Package, ChevronDown, ChevronUp } from 'lucide-react';

const Portfolio = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrollPosition, setScrollPosition] = useState(0);
  const [activeSection, setActiveSection] = useState('about');
  const [visibleCategories, setVisibleCategories] = useState({});

  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY);

      const sections = ['about', 'experience', 'projects', 'skills'];
      const currentSection = sections.find(section => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          return rect.top <= 100 && rect.bottom >= 100;
        }
        return false;
      });

      if (currentSection) {
        setActiveSection(currentSection);
      }
    };

    // Initialize all categories as visible
    const initialVisibility = groupedProjects ? Object.keys(groupedProjects).reduce((acc, category) => {
      acc[category] = true;
      return acc;
    }, {}) : {};
    setVisibleCategories(initialVisibility);

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleCategory = (category) => {
    setVisibleCategories(prev => ({
      ...prev,
      [category]: !prev[category]
    }));
  };

  const experiences = [
    {
      company: "Orange Health",
      role: "Software Engineer",
      period: "Jan 2022 – Jun 2023",
      achievements: [
        "Optimized order booking API performance by 30%",
        "Built a new microservice for payment operations",
        "Worked across 5 services, refactored 40% of the codebase",
        "Led projects including communication service, order funnel, and search improvements"
      ]
    },
    {
      company: "Cimpltech",
      role: "Software Engineer",
      period: "Jan 2021 – Dec 2021",
      achievements: [
        "Designed and implemented backend models and REST APIs",
        "Automated restaurant onboarding process, improving efficiency by 26%",
        "Enhanced QR code security by 76% through data encryption",
        "Implemented comprehensive API test coverage"
      ]
    },
    {
      company: "WaiterBee",
      role: "Software Engineer",
      period: "Mar 2019 – Dec 2020",
      achievements: [
        "Developed and deployed a full-stack web application",
        "Designed an intuitive user interface that enhanced customer satisfaction",
        "Created detailed reporting system increasing productivity by 15%"
      ]
    }
  ];

  const projects = [
    {
      name: "Spider Package Manager",
      tech: ["Python", "Selenium", "Windows", "Git"],
      description: "Developed a package manager for Windows Operating System with automated software installation capabilities. Features include dependency resolution, version management, and automated installations.",
      link: "#",
      github: "#",
      demo: "#",
      icon: <Package className="w-8 h-8 text-emerald-500" />,
      image: "/api/placeholder/800/600",
      category: "Developer Tools"
    },
    {
      name: "Groceries Cart",
      tech: ["Python", "Django", "PostgreSQL", "JavaScript"],
      description: "Full-stack web app enabling local store orders during Covid-19 with store dashboard functionality. Includes real-time order tracking and inventory management.",
      link: "#",
      github: "#",
      demo: "#",
      icon: <Layout className="w-8 h-8 text-purple-500" />,
      image: "/api/placeholder/800/600",
      category: "Full Stack"
    },
    {
      name: "Video Download Library",
      tech: ["Python", "Beautiful Soup", "Pytube"],
      description: "Library to automate course material downloads, tested with MIT OpenCourseWare. Features concurrent downloads and automatic organization.",
      link: "#",
      github: "#",
      demo: "#",
      icon: <Database className="w-8 h-8 text-blue-500" />,
      image: "/api/placeholder/800/600",
      category: "Developer Tools"
    },
    {
      name: "Popular Movies App",
      tech: ["Java", "Android Studio", "Retrofit"],
      description: "Android app to display popular and top-rated movies with trailer playback and favorites management. Includes offline support.",
      link: "#",
      github: "#",
      demo: "#",
      icon: <Star className="w-8 h-8 text-yellow-500" />,
      image: "/api/placeholder/800/600",
      category: "Mobile Development"
    },
    {
      name: "Task Management API",
      tech: ["Node.js", "Express", "MongoDB", "JWT"],
      description: "RESTful API for task management with authentication, task scheduling, and team collaboration features.",
      link: "#",
      github: "#",
      demo: "#",
      icon: <Code className="w-8 h-8 text-indigo-500" />,
      image: "/api/placeholder/800/600",
      category: "Backend Development"
    },
    {
      name: "Weather Dashboard",
      tech: ["React", "TypeScript", "Tailwind", "OpenWeather API"],
      description: "Interactive weather dashboard with location-based forecasts, historical data, and weather alerts.",
      link: "#",
      github: "#",
      demo: "#",
      icon: <Layout className="w-8 h-8 text-sky-500" />,
      image: "/api/placeholder/800/600",
      category: "Full Stack"
    }
  ];

  const groupedProjects = projects.reduce((acc, project) => {
    if (!acc[project.category]) {
      acc[project.category] = [];
    }
    acc[project.category].push(project);
    return acc;
  }, {});


  const skills = {
    "Languages": ["Python", "Go", "Java", "JavaScript", "TypeScript", "HTML", "CSS"],
    "Frameworks & Libraries": ["Django", "React.js", "Node.js", "Express", "Gin-Gorm", "Tailwind CSS"],
    "Tools & Technologies": ["Docker", "Kubernetes", "Git", "AWS", "PostgreSQL", "MongoDB"]
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Navigation */}
      <nav className={`fixed w-full z-50 transition-all duration-300 ${
        scrollPosition > 50 ? 'bg-gray-900/80 backdrop-blur-lg' : 'bg-transparent'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <span className="text-xl font-bold bg-gradient-to-r from-indigo-500 to-purple-500 bg-clip-text text-transparent">
                Dheeraj Srirama
              </span>
            </div>

            <div className="flex items-center md:hidden">
              <button
                onClick={() => setIsMenuOpen(!isMenuOpen)}
                className="text-gray-300 hover:text-white focus:outline-none"
                aria-label="Toggle menu"
              >
                {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
              </button>
            </div>

            <div className="hidden md:flex items-center space-x-8">
              {['about', 'experience', 'projects', 'skills'].map((item) => (
                <a
                  key={item}
                  href={`#${item}`}
                  className={`text-sm uppercase tracking-wider transition-all duration-300 ${
                    activeSection === item
                      ? 'text-indigo-400 font-semibold'
                      : 'text-gray-300 hover:text-white'
                  }`}
                >
                  {item}
                </a>
              ))}
              <div className="flex space-x-4">
                <a href="https://github.com" className="text-gray-300 hover:text-white transition-colors">
                  <Github size={20} />
                </a>
                <a href="https://linkedin.com" className="text-gray-300 hover:text-white transition-colors">
                  <Linkedin size={20} />
                </a>
                <a href="mailto:example@email.com" className="text-gray-300 hover:text-white transition-colors">
                  <Mail size={20} />
                </a>
              </div>
            </div>
          </div>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden absolute w-full bg-gray-900/95 backdrop-blur-lg">
            <div className="px-2 pt-2 pb-3 space-y-1">
              {['about', 'experience', 'projects', 'skills'].map((item) => (
                <a
                  key={item}
                  href={`#${item}`}
                  onClick={() => setIsMenuOpen(false)}
                  className={`block px-3 py-2 text-base font-medium ${
                    activeSection === item
                      ? 'text-indigo-400'
                      : 'text-gray-300 hover:text-white'
                  }`}
                >
                  {item}
                </a>
              ))}
            </div>
          </div>
        )}
      </nav>

      {/* Hero Section */}
      <section id="about" className="min-h-screen flex items-center justify-center">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-32 text-center">
          <h1 className="text-5xl sm:text-6xl md:text-7xl font-bold mb-8">
            <span className="bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 bg-clip-text text-transparent">
              Dheeraj Srirama
            </span>
          </h1>
          <p className="text-xl sm:text-2xl md:text-3xl text-gray-300 mb-12 max-w-3xl mx-auto">
            Product-oriented Software Engineer crafting innovative digital experiences
          </p>
          <div className="flex flex-col sm:flex-row justify-center gap-4">
            <a
              href="#projects"
              className="inline-flex items-center px-8 py-3 rounded-full bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-500 hover:to-purple-500 transition-all duration-300 text-white font-medium group"
            >
              View My Work
              <ArrowRight className="ml-2 group-hover:translate-x-1 transition-transform" />
            </a>
            <a
              href="#contact"
              className="inline-flex items-center px-8 py-3 rounded-full border border-indigo-500/50 hover:border-indigo-500 hover:bg-indigo-500/10 transition-all duration-300 text-white font-medium"
            >
              Get In Touch
            </a>
          </div>
        </div>
      </section>

      {/* Experience Section */}
      <section id="experience" className="py-32">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className="text-4xl font-bold mb-16 text-center">
            <span className="bg-gradient-to-r from-indigo-500 to-purple-500 bg-clip-text text-transparent">
              Experience
            </span>
          </h2>
          <div className="space-y-12">
            {experiences.map((exp, index) => (
              <div key={index} className="bg-gray-800/50 backdrop-blur-xl p-8 rounded-lg border border-gray-700/50 hover:border-indigo-500/50 transition-all duration-300">
                <h3 className="text-2xl font-semibold text-white">{exp.company}</h3>
                <p className="text-indigo-400 mb-2">{exp.role}</p>
                <p className="text-gray-400 text-sm mb-4">{exp.period}</p>
                <ul className="space-y-2">
                  {exp.achievements.map((achievement, i) => (
                    <li key={i} className="text-gray-300 flex items-start">
                      <span className="text-indigo-500 mr-2">•</span>
                      {achievement}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects Section */}
      <section id="projects" className="py-32 bg-gray-900/50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className="text-4xl font-bold mb-16 text-center">
            <span className="bg-gradient-to-r from-indigo-500 to-purple-500 bg-clip-text text-transparent">
              Projects
            </span>
          </h2>

          {Object.entries(groupedProjects).map(([category, categoryProjects]) => (
            <div key={category} className="mb-16 last:mb-0">
              <button
                onClick={() => toggleCategory(category)}
                className="w-full flex items-center justify-between text-2xl font-semibold text-white mb-8 border-b border-gray-700/50 pb-4 hover:text-indigo-400 transition-colors group"
              >
                <span>{category}</span>
                {visibleCategories[category] ? 
                  <ChevronUp className="w-6 h-6 text-indigo-400 group-hover:text-indigo-300 transition-transform duration-200" /> : 
                  <ChevronDown className="w-6 h-6 text-indigo-400 group-hover:text-indigo-300 transition-transform duration-200" />
                }
              </button>

              <div className={`grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 transition-all duration-300 ${
                visibleCategories[category] ? 'opacity-100 max-h-[2000px]' : 'opacity-0 max-h-0 overflow-hidden'
              }`}>
                {categoryProjects.map((project, index) => (
                  <div key={index} className="group bg-gray-800/50 backdrop-blur-xl rounded-lg border border-gray-700/50 hover:border-indigo-500/50 transition-all duration-300 overflow-hidden">
                    {/* Image Container */}
                    <div className="relative h-48 overflow-hidden">
                      <img
                        src={project.image}
                        alt={project.name}
                        className="w-full h-full object-cover transform group-hover:scale-105 transition-transform duration-300"
                      />
                      <div className="absolute inset-0 bg-gradient-to-t from-gray-900 via-gray-900/40 to-transparent opacity-60 group-hover:opacity-40 transition-opacity duration-300" />
                    </div>

                    {/* Content Container */}
                    <div className="p-6">
                      <div className="flex items-start justify-between mb-4">
                        <div className="flex items-center">
                          {project.icon}
                          <h3 className="text-lg font-semibold text-white ml-3">{project.name}</h3>
                        </div>
                        <div className="flex space-x-2">
                          <a 
                            href={project.github}
                            className="text-gray-400 hover:text-white transition-colors"
                            aria-label="View GitHub Repository"
                          >
                            <Github size={20} />
                          </a>
                          <a 
                            href={project.demo}
                            className="text-gray-400 hover:text-white transition-colors"
                            aria-label="View Live Demo"
                          >
                            <ExternalLink size={20} />
                          </a>
                        </div>
                      </div>
                      <div className="mb-4 flex flex-wrap gap-2">
                        {project.tech.map((tech, i) => (
                          <span
                            key={i}
                            className="px-2 py-1 text-xs bg-indigo-500/10 text-indigo-400 rounded-full border border-indigo-500/20"
                          >
                            {tech}
                          </span>
                        ))}
                      </div>
                      <p className="text-gray-300 text-sm">{project.description}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </section>
            {/* Skills Section */}
      <section id="skills" className="py-32">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className="text-4xl font-bold mb-16 text-center">
            <span className="bg-gradient-to-r from-indigo-500 to-purple-500 bg-clip-text text-transparent">
              Skills
            </span>
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {Object.entries(skills).map(([category, items]) => (
              <div key={category} className="bg-gray-800/50 backdrop-blur-xl p-8 rounded-lg border border-gray-700/50 hover:border-indigo-500/50 transition-all duration-300">
                <h3 className="text-xl font-semibold text-white mb-6">{category}</h3>
                <div className="flex flex-wrap gap-3">
                  {items.map((skill, index) => (
                    <span
                      key={index}
