import React, { useState, useEffect } from 'react';
import { ArrowUpRight, Code, Briefcase, BookOpen, Award, Github, Linkedin, Globe, Mail, Terminal } from 'lucide-react';

// CSS Animations
const animationCSS = `
  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes float {
    0% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
    100% { transform: translateY(0px); }
  }

  @keyframes float-slow {
    0% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
    100% { transform: translateY(0px); }
  }

  .animate-fadeIn {
    animation: fadeIn 0.6s ease-out forwards;
  }

  .animation-delay-100 {
    animation-delay: 0.1s;
  }

  .animation-delay-200 {
    animation-delay: 0.2s;
  }

  .animation-delay-300 {
    animation-delay: 0.3s;
  }

  .animation-delay-400 {
    animation-delay: 0.4s;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-float-slow {
    animation: float-slow 8s ease-in-out infinite;
  }
`;

// Section Title Component
const SectionTitle = ({ title, subtitle }) => {
  return (
    <div className="text-center mb-12">
      <p className="inline-block px-3 py-1 text-sm font-medium bg-indigo-100 dark:bg-indigo-900 text-indigo-600 dark:text-indigo-300 rounded-full mb-2 animate-fadeIn">{subtitle}</p>
      <h2 className="text-3xl md:text-4xl font-bold text-gray-800 dark:text-white mb-4 animate-fadeIn animation-delay-100">{title}</h2>
      <div className="w-24 h-1 bg-indigo-600 dark:bg-indigo-400 mx-auto rounded-full animate-fadeIn animation-delay-200"></div>
    </div>
  );
};

// Main App Component
const Portfolio = () => {
  const [activeSection, setActiveSection] = useState('home');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time
    setTimeout(() => {
      setIsLoading(false);
    }, 1500);

    // Setup scroll event listener for nav highlighting
    const handleScroll = () => {
      const sections = document.querySelectorAll('section');
      const scrollPosition = window.scrollY + 300;

      sections.forEach(section => {
        const sectionTop = section.offsetTop;
        const sectionHeight = section.offsetHeight;
        const sectionId = section.getAttribute('id');

        if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
          setActiveSection(sectionId);
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  if (isLoading) {
    return <LoadingScreen />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-white dark:from-gray-900 dark:to-gray-800 text-gray-800 dark:text-gray-100">
      <style>{animationCSS}</style>
      <Navbar activeSection={activeSection} />

      <main className="pt-20">
        <HeroSection />
        <AboutSection />
        <ExperienceSection />
        <ProjectsSection />
        <SkillsSection />
        <EducationSection />
        <AchievementsSection />
        <ContactSection />
      </main>

      <Footer />
    </div>
  );
};

// Loading Screen Component
const LoadingScreen = () => {
  return (
    <div className="h-screen w-full flex flex-col items-center justify-center bg-gradient-to-br from-indigo-500 to-purple-600 text-white">
      <div className="relative w-24 h-24">
        <div className="absolute w-full h-full border-8 border-white border-opacity-20 rounded-full"></div>
        <div className="absolute w-full h-full border-8 border-transparent border-t-white rounded-full animate-spin"></div>
      </div>
      <h1 className="mt-8 text-2xl font-bold tracking-wider">DHEERAJ SRIRAMA</h1>
      <p className="mt-2 text-white text-opacity-80">Software Engineer</p>
    </div>
  );
};

// Navbar Component
const Navbar = ({ activeSection }) => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navLinks = [
    { id: 'home', label: 'Home' },
    { id: 'about', label: 'About' },
    { id: 'experience', label: 'Experience' },
    { id: 'projects', label: 'Projects' },
    { id: 'skills', label: 'Skills' },
    { id: 'education', label: 'Education' },
    { id: 'contact', label: 'Contact' }
  ];

  return (
    <nav className={`fixed top-0 left-0 w-full z-50 transition-all duration-300 ${isScrolled ? 'bg-white dark:bg-gray-900 shadow-md py-2' : 'bg-transparent py-4'}`}>
      <div className="container mx-auto px-4 md:px-8 flex justify-between items-center">
        <a href="#home" className="text-xl font-bold text-indigo-600 dark:text-indigo-400">DS</a>

        {/* Desktop Navigation */}
        <div className="hidden md:flex space-x-8">
          {navLinks.map(link => (
            <a 
              key={link.id}
              href={`#${link.id}`}
              className={`transition-all duration-300 hover:text-indigo-600 dark:hover:text-indigo-400 ${activeSection === link.id ? 'text-indigo-600 dark:text-indigo-400 font-medium' : 'text-gray-600 dark:text-gray-300'}`}
            >
              {link.label}
            </a>
          ))}
        </div>

        {/* Mobile Menu Button */}
        <button 
          className="md:hidden text-gray-600 dark:text-gray-300 focus:outline-none"
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            {isMobileMenuOpen ? (
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            ) : (
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            )}
          </svg>
        </button>
      </div>

      {/* Mobile Navigation */}
      <div className={`md:hidden transition-all duration-300 overflow-hidden ${isMobileMenuOpen ? 'max-h-screen opacity-100 shadow-xl' : 'max-h-0 opacity-0'}`}>
        <div className="bg-white dark:bg-gray-800 px-4 py-2">
          {navLinks.map(link => (
            <a 
              key={link.id}
              href={`#${link.id}`}
              className={`block py-2 transition-all duration-300 hover:text-indigo-600 dark:hover:text-indigo-400 ${activeSection === link.id ? 'text-indigo-600 dark:text-indigo-400 font-medium' : 'text-gray-600 dark:text-gray-300'}`}
              onClick={() => setIsMobileMenuOpen(false)}
            >
              {link.label}
            </a>
          ))}
        </div>
      </div>
    </nav>
  );
};

// Hero Section Component
const HeroSection = () => {
  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative overflow-hidden">
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-20 left-20 w-64 h-64 bg-indigo-400 rounded-full opacity-10 filter blur-3xl animate-float-slow"></div>
        <div className="absolute bottom-20 right-20 w-80 h-80 bg-purple-400 rounded-full opacity-10 filter blur-3xl animate-float"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-blue-400 rounded-full opacity-10 filter blur-3xl animate-pulse"></div>
      </div>

      <div className="container mx-auto px-4 md:px-8 relative z-10">
        <div className="text-center">
          <p className="inline-block px-3 py-1 text-sm font-medium bg-indigo-100 dark:bg-indigo-900 text-indigo-600 dark:text-indigo-300 rounded-full mb-6 animate-fadeIn">Software Engineer</p>
          <h1 className="text-4xl md:text-6xl font-bold mb-4 tracking-tight animate-fadeIn animation-delay-100">
            <span className="text-gray-800 dark:text-white">I'm </span>
            <span className="text-indigo-600 dark:text-indigo-400">Dheeraj Srirama</span>
          </h1>
          <p className="text-lg md:text-xl text-gray-600 dark:text-gray-300 max-w-2xl mx-auto mb-8 animate-fadeIn animation-delay-200">
            Building elegant solutions to complex problems with Go, Python, and JavaScript.
          </p>

          <div className="flex flex-wrap justify-center gap-4 animate-fadeIn animation-delay-300">
            <a 
              href="#contact" 
              className="px-6 py-3 bg-indigo-600 text-white font-medium rounded-lg flex items-center gap-2 hover:bg-indigo-700 transition-all"
            >
              Get in touch <ArrowUpRight size={18} />
            </a>
            <a 
              href="#projects" 
              className="px-6 py-3 bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 font-medium rounded-lg hover:bg-gray-300 dark:hover:bg-gray-600 transition-all"
            >
              View my work
            </a>
          </div>

          <div className="flex justify-center gap-6 mt-12 animate-fadeIn animation-delay-400">
            <a href="https://github.com/dheerajram13" target="_blank" rel="noopener noreferrer" className="text-gray-600 dark:text-gray-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-all">
              <Github size={24} />
            </a>
            <a href="https://linkedin.com/in/dheerajsrirama" target="_blank" rel="noopener noreferrer" className="text-gray-600 dark:text-gray-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-all">
              <Linkedin size={24} />
            </a>
            <a href="mailto:sriramadheeraj@gmail.com" className="text-gray-600 dark:text-gray-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-all">
              <Mail size={24} />
            </a>
            <a href="#" target="_blank" rel="noopener noreferrer" className="text-gray-600 dark:text-gray-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-all">
              <Globe size={24} />
            </a>
          </div>
        </div>
      </div>

      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
        <a href="#about" className="flex flex-col items-center text-gray-500 dark:text-gray-400 hover:text-indigo-600 dark:hover:text-indigo-400 transition-all">
          <span className="text-sm mb-2">Scroll Down</span>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
          </svg>
        </a>
      </div>
    </section>
  );
};

// About Section Component
const AboutSection = () => {
  return (
    <section id="about" className="py-20 relative">
      <div className="absolute inset-0 bg-indigo-50 dark:bg-gray-800 skew-y-3 z-0"></div>
      <div className="container mx-auto px-4 md:px-8 relative z-10">
        <SectionTitle title="About Me" subtitle="Get to know me better" />

        <div className="grid md:grid-cols-2 gap-12 items-center">
          <div className="relative">
            <div className="w-full h-80 bg-indigo-200 dark:bg-indigo-900 rounded-xl overflow-hidden relative animate-fadeIn">
              <div className="absolute inset-0 bg-gradient-to-tr from-indigo-500 to-purple-600 opacity-80"></div>
              <div className="absolute inset-0 flex items-center justify-center">
                <Terminal className="w-24 h-24 text-white" />
              </div>
            </div>
            <div className="absolute -bottom-6 -right-6 w-32 h-32 bg-white dark:bg-gray-700 rounded-lg shadow-xl p-4 flex items-center justify-center animate-fadeIn animation-delay-200">
              <div className="text-center">
                <div className="font-bold text-3xl text-indigo-600 dark:text-indigo-400">4+</div>
                <div className="text-sm text-gray-600 dark:text-gray-300">Years of Experience</div>
              </div>
            </div>
          </div>

          <div className="animate-fadeIn animation-delay-100">
            <h3 className="text-2xl font-bold mb-4 text-gray-800 dark:text-white">Software Engineer specialized in backend development with experience in Go, Python, and JavaScript</h3>
            <p className="text-gray-600 dark:text-gray-300 mb-6">
              Currently pursuing my Master's in Computer Science with a focus on Management at The University of Queensland.
              I have over 4 years of professional experience working across various domains and technologies.
            </p>
            <p className="text-gray-600 dark:text-gray-300 mb-6">
              My expertise lies in designing and developing scalable applications, with a particular focus on backend technologies
              and microservices architecture. I enjoy solving complex problems and optimizing systems for better performance.
            </p>

            <div className="grid grid-cols-2 gap-4">
              <div className="flex items-center">
                <span className="w-2 h-2 bg-indigo-600 dark:bg-indigo-400 rounded-full mr-2"></span>
                <span className="text-gray-600 dark:text-gray-300">API Development</span>
              </div>
              <div className="flex items-center">
                <span className="w-2 h-2 bg-indigo-600 dark:bg-indigo-400 rounded-full mr-2"></span>
                <span className="text-gray-600 dark:text-gray-300">Microservices</span>
              </div>
              <div className="flex items-center">
                <span className="w-2 h-2 bg-indigo-600 dark:bg-indigo-400 rounded-full mr-2"></span>
                <span className="text-gray-600 dark:text-gray-300">Database Optimization</span>
              </div>
              <div className="flex items-center">
                <span className="w-2 h-2 bg-indigo-600 dark:bg-indigo-400 rounded-full mr-2"></span>
                <span className="text-gray-600 dark:text-gray-300">Cloud Infrastructure</span>
              </div>
              <div className="flex items-center">
                <span className="w-2 h-2 bg-indigo-600 dark:bg-indigo-400 rounded-full mr-2"></span>
                <span className="text-gray-600 dark:text-gray-300">Full-Stack Development</span>
              </div>
              <div className="flex items-center">
                <span className="w-2 h-2 bg-indigo-600 dark:bg-indigo-400 rounded-full mr-2"></span>
                <span className="text-gray-600 dark:text-gray-300">LLM Integration</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

// Experience Section Component
const ExperienceSection = () => {
  const experiences = [
    {
      title: "Software Engineer",
      company: "Orange Health",
      period: "Jan 2022 - Jun 2023",
      description: [
        "Optimized order booking API performance by 30% through parallel processing in Go and database optimizations.",
        "Built a new microservice for payment operations, including payments, refunds, payouts, and settlements, leveraging Python (Django) and Celery for task management.",
        "Refactored 40% of the codebase, enhancing maintainability and reducing incidents using Redis and RabbitMQ.",
        "Led projects including communication service, order funnel, and search improvements."
      ],
      tech: ["Go", "Python", "Django", "Celery", "Redis", "RabbitMQ"]
    },
    {
      title: "Software Engineer",
      company: "Cimpltech",
      period: "Jan 2021 - Dec 2021",
      description: [
        "Developed backend models and REST APIs for customer app and dashboard using Python and Django.",
        "Improved efficiency by 26% by automating the onboarding process using Python scripting and workflow automation tools.",
        "Improved productivity of customers by 17% through implementing push notifications using Google Firebase.",
        "Reduced customer complaints by 16%, implementing comprehensive API test coverage."
      ],
      tech: ["Python", "Django", "REST API", "Firebase"]
    },
    {
      title: "Software Engineer",
      company: "WaiterBee",
      period: "Mar 2019 - Dec 2020",
      description: [
        "Developed a full-stack web application using Python, Django, HTML, CSS, Javascript.",
        "Enhanced QR code security by 75% with a hybrid AES-RSA encryption, significantly improving data protection.",
        "Created reporting system for sales, products, orders, and employee metrics, increasing productivity by 15%."
      ],
      tech: ["Python", "Django", "HTML/CSS", "JavaScript", "Encryption"]
    }
  ];

  return (
    <section id="experience" className="py-20">
      <div className="container mx-auto px-4 md:px-8">
        <SectionTitle title="Work Experience" subtitle="My professional journey" />

        <div className="relative">
          {/* Timeline line */}
          <div className="absolute top-0 left-6 md:left-1/2 w-0.5 h-full bg-indigo-200 dark:bg-gray-700 transform -translate-x-1/2"></div>

          {experiences.map((exp, index) => (
            <div 
              key={index}
              className={`mb-12 relative animate-fadeIn ${index % 2 === 0 ? 'md:pr-12 md:text-right' : 'md:pl-12'} ${index === 0 ? '' : 'animation-delay-100'} ${index === 2 ? 'animation-delay-200' : ''}`}
            >
              <div className={`md:w-1/2 ${index % 2 === 0 ? 'md:mr-auto' : 'md:ml-auto'}`}>
                <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg hover:shadow-xl transition-all hover:-translate-y-1 border border-gray-100 dark:border-gray-700">
                  <div className="absolute top-6 left-6 md:left-1/2 w-6 h-6 bg-indigo-600 dark:bg-indigo-400 rounded-full transform -translate-x-1/2 flex items-center justify-center">
                    <div className="w-3 h-3 bg-white rounded-full"></div>
                  </div>

                  <span className="inline-block px-3 py-1 text-xs font-medium bg-indigo-100 dark:bg-indigo-900 text-indigo-600 dark:text-indigo-300 rounded-full mb-2">{exp.period}</span>
                  <h3 className="text-xl font-bold mb-1 text-gray-800 dark:text-white">{exp.title}</h3>
                  <h4 className="text-indigo-600 dark:text-indigo-400 font-medium mb-4">{exp.company}</h4>

                  <ul className="text-gray-600 dark:text-gray-300 mb-4 space-y-2">
                    {exp.description.map((item, i) => (
                      <li key={i} className="flex items-start">
                        <span className="inline-block w-2 h-2 bg-indigo-600 dark:bg-indigo-400 rounded-full mr-2 mt-2 flex-shrink-0"></span>
                        <span>{item}</span>
                      </li>
                    ))}
                  </ul>

                  <div className="flex flex-wrap gap-2">
                    {exp.tech.map((tech, i) => (
                      <span key={i} className="px-2 py-1 text-xs bg-gray-100 dark:bg-gray-700 text-gray-600 dark:text-gray-300 rounded">{tech}</span>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Projects Section Component
const ProjectsSection = () => {
  const projects = [
    {
      title: "Job Application Tracker",
      period: "Jan 2025 - Present",
      description: "Developed a job application tracking website and chrome extension to reduce job tracking time by 75% through one-click URL capture. Leveraged LLM to automate job detail extraction and storage in DB.",
      tech: ["Python", "FastAPI", "PostgreSQL", "React", "Docker", "LLM"],
      tags: ["Full Stack", "AI Integration"],
      image: "job-tracker"
    },
    {
      title: "LLM for Network Traffic Generation",
      period: "Sep 2024 - Present",
      description: "Researching synthetic network traffic generation using Large Language Models (LLMs) like GPT-4 to simulate diverse protocols and attack patterns for improved intrusion detection. Addressing challenges in existing datasets, such as 75% imbalance in benign vs. malicious traffic, to enhance the training of Network Intrusion Detection Systems (NIDS).",
      tech: ["GPT-4", "Mininet", "Python", "Docker", "Linux"],
      tags: ["Machine Learning", "AI Research", "Network Security"],
      image: "network-traffic"
    },
    {
      title: "CourseCompanion",
      period: "2023 - 2024",
      description: "Developed a platform for students to upload study materials, collaborate, and seek help. Featuring material management, peer connection, discussion space, and resource exchange to address challenges in managing resources and connecting with peers.",
      tech: ["React.js", "Node.js", "Express", "Firebase", "HTML/CSS"],
      tags: ["Full Stack", "Web App", "Education"],
      image: "course-companion"
    },
    {
      title: "QuickSettle",
      period: "2023",
      description: "Created an extension to the Westpac mobile app allowing users to quickly settle transactions. Features include automation for detecting individual or group payments, seamless integration with multiple payment methods, and customizable payment links generation.",
      tech: ["Mobile App Development", "Payment API Integration", "UX Design"],
      tags: ["FinTech", "Mobile App", "UX/UI"],
      image: "quick-settle"
    },
    {
      title: "Spider Package Manager",
      period: "Feb 2018 - May 2018",
      description: "Developed a package manager for Windows OS to install, upgrade, configure, and use software. Tested software installations and uninstallations using PyTest, ensuring compatibility across system architectures.",
      tech: ["Python", "Selenium", "Windows", "Git", "PyTest"],
      tags: ["Desktop App", "System Utility"],
      image: "spider-manager"
    }
  ];

  // Function to get image placeholder based on project type
  const getProjectImage = (imageKey) => {
    // Using placeholder images with dimensions that work well for project cards
    // In a real implementation, you would replace these with actual project screenshots
    const imageMap = {
      "job-tracker": "/api/placeholder/800/600",
      "network-traffic": "/api/placeholder/800/600",
      "course-companion": "/api/placeholder/800/600",
      "quick-settle": "/api/placeholder/800/600",
      "spider-manager": "/api/placeholder/800/600"
    };

    return imageMap[imageKey] || "/api/placeholder/800/600";
  };

  return (
    <section id="projects" className="py-20 bg-gray-50 dark:bg-gray-800">
